export const fetchFromAPI = async (url) => {
  // const { data } = await axios.get(`${BASE_URL}/${url}`, options);
  const { data } = await axios.get(`https://www.googleapis.com/youtube/v3/videos?part=snippet%2CcontentDetails%2Cstatistics&chart=mostPopular&maxResults=15&regionCode=FR&key=${process.env.REACT_APP_YOUTUBE_API_KEY}`);


  return data;
};
